import styled from "styled-components";

export const Container=styled.div`
//margin-bottom: 100px;
`

export const Nav=styled.div`
display: flex;
justify-content: space-between;
flex-direction:row;
align-items: center;
`

export const NavLogo=styled.div`
display: flex;
`

export const Logo=styled.img`
width: 37px;
height: 45px;
`

export const Navs=styled.div`
display: flex;
flex-direction:row;
align-items: center;
width: 500px;
justify-content: space-between;
border: 1px solid green;
`

export const Sign=styled.div`
font-family: Poppins;
font-style: normal;
font-weight: 500;
font-size: 24px;
line-height: 37px;
color: #FFFFFF;
background: #58A9A5;
border-radius: 15px;
width: 251px;
height: 75px;
box-sizing: border-box;
display: flex;
align-items: center;
justify-content: center;
`
export const Li=styled.div`
font-family: Poppins;
font-style: normal;
font-weight: normal;
font-size: 23px;
line-height: 37px;
color: #404242;
`
