import styled from "styled-components";

export const Container=styled.div`
//margin-bottom: 100px;
`

export const Nav=styled.div`
display: flex;
justify-content: space-between;
flex-direction:row;
align-items: center;
`

export const NavLogo=styled.div`
display: flex;
`

export const Logo=styled.img`
width: 37px;
height: 45px;
margin-right: 14px;
`
export const Text=styled.div`
font-family: Poppins;
font-style: normal;
font-weight: 800;
font-size: 35px;
line-height: 52px;
color: #58A9A5;
`

export const Navs=styled.div`
display: flex;
flex-direction:row;
align-items: center;
width: 500px;
justify-content: space-between;
border: 1px solid green;
`

export const Sign=styled.div`
font-family: Poppins;
font-style: normal;
font-weight: 500;
font-size: 24px;
color: #FFFFFF;
background: #58A9A5;
border-radius: 15px;
width: 241px;
height: 83px;
box-sizing: border-box;
display: flex;
align-items: center;
justify-content: center;
`
export const Li=styled.div`
font-family: Poppins;
font-style: normal;
font-weight: normal;
font-size: 23px;
line-height: 37px;
color: #404242;
cursor: pointer;
&:hover{
width: 48px;
height: 6px;
border-radius: 21px;
margin-bottom: 7px;
border-bottom: 3px solid   #FEC62D;
cursor: pointer;
}
`
